import { createPortal } from 'react-dom';

export let RenderToBody = ({ children }: { children: React.ReactElement }) => {
  if (typeof window == 'undefined') return null;
  return createPortal(children, document.body);
};
import { styled, keyframes } from 'goober';
import React, { createRef, useCallback, useEffect, useState } from 'react';
import { usePopper } from 'react-popper';
import useDelayed from 'use-delayed';
import outsideClick from '@varld/outside-click';
import { RenderToBody } from './portal';

let Wrapper = styled('div', React.forwardRef)`
  z-index: 9999;
  pointer-events: none;

  &.open {
    pointer-events: all;
  }
`;

let Inner = styled('div')`
  padding: 7px;
  border-radius: 9px;
  border: solid #efefef 1px;
  background: white;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.12);
  opacity: 1;
`;

let fadeIn = keyframes`
  from {
    opacity: 0;
    margin-top: 0px;
  }

  to {
    opacity: 1;
    margin-top: 6px;
  }
`;

let fadeOut = keyframes`
  from {
    opacity: 1;
    margin-top: 6px;
  }

  to {
    opacity: 0;
    margin-top: 0px;
  }
`;

export let Popover = React.forwardRef(
  (
    {
      popover,
      children,
    }: {
      popover: (d: {
        visible: boolean;
        open: boolean;
        close: () => void;
      }) => React.ReactElement;
      children: React.ReactElement | React.ReactElement[];
    },
    ref: any
  ) => {
    let [referenceElement, setReferenceElement] = useState(null) as any;
    let [popperElement, setPopperElement] = useState(null) as any;
    let { styles, attributes } = usePopper(referenceElement, popperElement);
    let [open, setOpen] = useState(false);
    let visible = useDelayed(open, 500, [true]);
    let close = useCallback(() => setOpen(false), [setOpen]);

    let popoverEl = popover({ visible, open, close });

    useEffect(() => {
      return outsideClick(
        [referenceElement, popperElement],
        () => setOpen(false),
        () => open
      );
    }, [referenceElement, popperElement, open]);

    useEffect(() => {
      if (!ref) ref = createRef();
      ref.current = { setOpen };
    }, [ref, setOpen]);

    return (
      <>
        <div
          tabIndex={0}
          ref={setReferenceElement}
          onClick={() => setOpen(!open)}
          style={{
            width: 'fit-content',
            height: 'fit-content',
          }}
          data-popover-anchor
        >
          {children}
        </div>

        {visible && (
          <RenderToBody>
            <Wrapper
              className={open ? 'open' : ''}
              ref={setPopperElement}
              style={styles.popper}
              {...attributes.popper}
            >
              <Inner
                style={{
                  animation: `${
                    open ? fadeIn : fadeOut
                  } 0.2s ease-in-out forwards`,
                }}
              >
                {popoverEl}
              </Inner>
            </Wrapper>
          </RenderToBody>
        )}
      </>
    );
  }
);
import { keyframes, styled } from 'goober';
import React, { useEffect, useRef, useState } from 'react';
import { usePopper } from 'react-popper';
import useDelayed from 'use-delayed';
import outsideClick from '@varld/outside-click';

let Wrapper = styled('div', React.forwardRef)`
  width: fit-content;
  z-index: 9999;
  pointer-events: none;

  &.open {
    pointer-events: all;
  }
`;

let Inner = styled('div')`
  border: solid #efefef 1px;
  border-radius: 5px;
  padding: 6px 10px;
  background: white;
  color: black;
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.12);
  font-size: 0.8em;
  font-weight: 500;
`;

let fadeIn = keyframes`
  from {
    opacity: 0;
    margin-top: 0px;
  }

  to {
    opacity: 1;
    margin-top: 5px;
  }
`;

let fadeOut = keyframes`
  from {
    opacity: 1;
    margin-top: 5px;
  }

  to {
    opacity: 0;
    margin-top: 0px;
  }
`;

export let Tooltip = ({
  content,
  children,
}: {
  content: string;
  children: React.ReactElement | React.ReactElement[];
}) => {
  let [referenceElement, setReferenceElement] = useState(null) as any;
  let [popperElement, setPopperElement] = useState(null) as any;
  let { styles, attributes } = usePopper(referenceElement, popperElement);
  let [open, setOpen] = useState(false);
  let visible = useDelayed(open, 500, [true]);
  let enterToRef = useRef<any>();

  useEffect(() => {
    return outsideClick(
      [referenceElement, popperElement],
      () => setOpen(false),
      () => open
    );
  }, [referenceElement, popperElement, open]);

  return (
    <>
      <div
        tabIndex={0}
        ref={setReferenceElement}
        onClick={() => {
          if (open) clearTimeout(enterToRef.current);
          setOpen(true);
        }}
        onMouseEnter={() => {
          clearTimeout(enterToRef.current);
          enterToRef.current = setTimeout(() => {
            setOpen(true);
          }, 300);
        }}
        onMouseLeave={() => {
          clearTimeout(enterToRef.current);
          setOpen(false);
        }}
        style={{
          width: 'fit-content',
          height: 'fit-content',
        }}
      >
        {children}
      </div>

      {visible && (
        <Wrapper
          className={open ? 'open' : ''}
          ref={setPopperElement}
          style={styles.popper}
          {...attributes.popper}
        >
          <Inner
            style={{
              animation: `${open ? fadeIn : fadeOut} 0.2s ease-in-out forwards`,
            }}
          >
            {content}
          </Inner>
        </Wrapper>
      )}
    </>
  );
};
import { setup } from 'goober';
import * as React from 'react';

setup(React.createElement);

export * from './popover';
export * from './tooltip';
export * from './portal';
